function [errx]=construn(A,sigma,alpha,RunL)iters=RunL;tol=0.1;k=10;d=2;I=eye(d);last=1;x=zeros(d,iters);x_mean=zeros(d,iters);x(:,1)=rand(d,1);x(:,1)=zeros(d,1);%x(:,1)=x(:,1)/norm(x(:,1));x_mean(:,1)=x(:,1);errx(1)=norm(x(1))^2;b=A*[1;1];%b=[1;1];for i=2:itersn=0*(rand-0.5);At=A+sigma*(rand(d)-0.5);x(:,i)=x(:,i-1)+alpha*(n+b -At*x(:,i-1));x_mean(:,i)=mean(x(:,1:i),2);errx(i)=norm(x_mean(:,i)-inv(A)*b)^2;end;