clear all;close all;iters=20;runs=100;errx=zeros(runs,iters);for i=1:runs[errx(i,:),erry(i,:),errz(i,:)]=flsa(iters);end;mx=mean(errx);my=mean(erry);mz=mean(errz);fptr=fopen('as','w');for i=1:itersfprintf(fptr,'%e\t%e\n',i,mx(i));endfclose(fptr);fptr=fopen('pd','w');for i=1:itersfprintf(fptr,'%e\t%e\n',i,my(i));endfclose(fptr);fptr=fopen('spd','w');for i=1:itersfprintf(fptr,'%e\t%e\n',i,mz(i));endfclose(fptr);