clear all;close all;alpha=load('hur_data');iters=2;sigma=[0 2 5 10 20];RunL=1000;err=zeros(iters,RunL);A=[1 -10;10 1]; for j=1:5fptr=fopen(strcat('hur',num2str(j)),'w');err=zeros(iters,RunL);  for i=1:iters      [err(i,:)]=construn(A,sigma(j),alpha(j,2),RunL);  end;err_mean=mean(err);err_std=std(err);  for i=1:RunL%    fprintf(fptr,'%d\t%e\t%e\n',i,err_mean(i),err_std(i));    fprintf(fptr,'%d\t%e\n',i,err_mean(i));  end;  fclose(fptr);  end;clear all;close all;alpha=load('pd_data');iters=2;sigma=[0 2 5 10 20];RunL=10000;errx=zeros(iters,RunL);A=[1 10;0 1]; %U as belowfor j=1:5fptr=fopen(strcat('pd',num2str(j)),'w');  for i=1:iters  err=zeros(iters,RunL);      [err(i,:)]=construn(A,sigma(j),alpha(j,2),RunL);  end;err_mean=mean(err);err_std=std(err);  for i=1:RunL    fprintf(fptr,'%d\t%e\t%e\n',i,err_mean(i),err_std(i));  end;  fclose(fptr);  end;