close all;clear all;iters=20;sigma=[0 2 5 10 20];A=[10 -100; 100 10]; %No U requiredA=[1 -10; 10 1]; %No U required%A=[1 0; 0 1]; %No U required%A=[10 100;0 10]; %U as belowU=[1 0; 0 0.1]fptr=fopen('hur','w');fptrd=fopen('hur_data','w');fp=fopen('hur_last','w');fprintf(fptr,'X Y Y_error\n');for j=1:length(sigma)  for i=1:iters      [alpha(i),last(i)]=stab(A,sigma(j));  end;fprintf(fptr,'%d\t%e\t%e\n',sigma(j),mean(alpha),std(alpha));fprintf(fptrd,'%d\t%e\t%e\n',sigma(j),mean(alpha),std(alpha));fprintf(fp,'%d\t%d\n',sigma(j),mean(last));end;fclose(fptr);fclose(fp);fclose(fptrd);close all;clear all;iters=20;sigma=[0 2 5 10 20];A=[1 10;0 1]; %U as belowU=[1 0; 0 0.1];fptr=fopen('pd','w');fptrd=fopen('pd_data','w');fp=fopen('pd_last','w');fprintf(fptr,'X Y Y_error\n');for j=1:length(sigma)  for i=1:iters      [alpha(i),last(i)]=stab(A,sigma(j));  end;fprintf(fptr,'%d\t%e\t%e\n',sigma(j),mean(alpha),std(alpha));fprintf(fptrd,'%d\t%e\t%e\n',sigma(j),mean(alpha),std(alpha));fprintf(fp,'%d\t%d\n',sigma(j),mean(last));end;fclose(fptr);fclose(fptrd);fclose(fp);