function [alpha,last]=stab(A,sigma)iters=1000;alpha=1;tol=0.025;k=10;d=2;I=eye(d);last=1;x=zeros(d,iters);x_mean=zeros(d,iters);x(:,1)=rand(d,1);x(:,1)=x(:,1)/norm(x(:,1));x_mean(:,1)=x(:,1);errx(1)=norm(x(1))^2;b=A*[1;1];for i=2:itersn=0.5*(rand-0.5);At=A+sigma*(rand(d)-0.5);x(:,i)=x(:,i-1)+alpha*(n+b -At*x(:,i-1));  if(mod(i,k)==0) %Peridoically check every k iterations      r_1=(norm(x(:,i-k/2))/norm(x(:,i-k+1)));      r_2=(norm(x(:,i))/norm(x(:,i-k/2)));    if( r_2/r_1> (1+tol))     alpha=alpha/2;     last=i;     x(:,i)=zeros(d,1);    end;  end;x_mean(:,i)=mean(x(:,1:i),2);errx(i)=norm(x(:,i))^2;end;